mapper.select:
SELECT count(boardKey) FROM board b JOIN customer c ON c.custKey = b.custKey JOIN item i ON i.itemKey = b.itemKey WHERE b.ntype = 'q'
AND ${type} LIKE CONCAT('%', #{keyword}, '%')
AND boardKey = #{keyword}

SELECT b.boardKey AS "boardKey", b.custKey AS "custKey", b.itemKey AS "itemKey", b.ntype AS "ntype", b.title AS "title", b.rdate AS "rdate", b.content AS "content" , b.img AS "img", b.rate AS "rate", i.name AS "name", i.price AS "price" , c.username AS "username" FROM board b JOIN customer c ON c.custKey = b.custKey JOIN item i ON i.itemKey = b.itemKey WHERE b.ntype = 'q'
AND boardKey = #{keyword}
AND ${type} LIKE CONCAT('%', #{keyword}, '%')
ORDER BY boardKey DESC LIMIT #{skip}, #{amount}

SELECT count(*) FROM board b JOIN customer c ON c.custKey = b.custKey JOIN item i ON i.itemKey = b.itemKey WHERE b.ntype = 'r'
AND ${type} LIKE CONCAT('%', #{keyword}, '%')
AND boardKey = #{keyword}

